'use client';

import { motion, AnimatePresence } from 'framer-motion';
import { createContext, useContext, useState, ReactNode } from 'react';
import { ChevronDown } from 'lucide-react';
import { clsx } from 'clsx';

type AccordionType = 'single' | 'multiple';

interface AccordionContextValue {
  type: AccordionType;
  openItems: Set<string>;
  toggleItem: (value: string) => void;
  isOpen: (value: string) => boolean;
}

const AccordionContext = createContext<AccordionContextValue | null>(null);

function useAccordionContext() {
  const context = useContext(AccordionContext);
  if (!context) {
    throw new Error('Accordion components must be used within an Accordion');
  }
  return context;
}

interface AccordionProps {
  type?: AccordionType;
  defaultOpen?: string | string[];
  children: ReactNode;
  className?: string;
}

export function Accordion({
  type = 'single',
  defaultOpen = [],
  children,
  className,
}: AccordionProps) {
  const initialOpen = Array.isArray(defaultOpen) ? defaultOpen : [defaultOpen];
  const [openItems, setOpenItems] = useState<Set<string>>(new Set(initialOpen));

  const toggleItem = (value: string) => {
    setOpenItems((prev) => {
      const next = new Set(prev);
      if (next.has(value)) {
        next.delete(value);
      } else {
        if (type === 'single') {
          next.clear();
        }
        next.add(value);
      }
      return next;
    });
  };

  const isOpen = (value: string) => openItems.has(value);

  return (
    <AccordionContext.Provider value={{ type, openItems, toggleItem, isOpen }}>
      <div className={clsx('space-y-1', className)}>{children}</div>
    </AccordionContext.Provider>
  );
}

interface AccordionItemProps {
  value: string;
  children: ReactNode;
  className?: string;
}

export function AccordionItem({ value, children, className }: AccordionItemProps) {
  return (
    <div
      className={clsx(
        'border border-gray-700/50 rounded-lg bg-gray-800/30 overflow-hidden',
        className
      )}
    >
      {children}
    </div>
  );
}

interface AccordionTriggerProps {
  children: ReactNode;
  icon?: ReactNode;
  showIndicator?: boolean;
  badge?: ReactNode;
  className?: string;
}

export function AccordionTrigger({
  children,
  icon,
  showIndicator = true,
  badge,
  className,
}: AccordionTriggerProps) {
  const { toggleItem, isOpen } = useAccordionContext();
  const parentValue = useAccordionItemContext();
  const open = isOpen(parentValue);

  return (
    <button
      onClick={() => toggleItem(parentValue)}
      className={clsx(
        'w-full flex items-center gap-2.5 px-3 py-2.5',
        'text-left text-sm font-medium text-white',
        'hover:bg-gray-700/30 transition-colors',
        'focus:outline-none focus-visible:ring-2 focus-visible:ring-cyan-500/50 focus-visible:ring-inset',
        className
      )}
    >
      {icon && (
        <span className="flex-shrink-0 w-4 h-4 text-gray-400">{icon}</span>
      )}
      <span className="flex-1 min-w-0">{children}</span>
      {badge && <span className="flex-shrink-0">{badge}</span>}
      {showIndicator && (
        <motion.div
          animate={{ rotate: open ? 180 : 0 }}
          transition={{ duration: 0.2 }}
          className="flex-shrink-0"
        >
          <ChevronDown className="w-4 h-4 text-gray-400" />
        </motion.div>
      )}
    </button>
  );
}

interface AccordionContentProps {
  children: ReactNode;
  className?: string;
}

export function AccordionContent({ children, className }: AccordionContentProps) {
  const { isOpen } = useAccordionContext();
  const parentValue = useAccordionItemContext();
  const open = isOpen(parentValue);

  return (
    <AnimatePresence initial={false}>
      {open && (
        <motion.div
          initial={{ height: 0, opacity: 0 }}
          animate={{ height: 'auto', opacity: 1 }}
          exit={{ height: 0, opacity: 0 }}
          transition={{ duration: 0.2, ease: 'easeInOut' }}
          className="overflow-hidden"
        >
          <div className={clsx('px-3 py-2.5 text-sm text-gray-300', className)}>
            {children}
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

// Context for AccordionItem value
const AccordionItemContext = createContext<string>('');

function useAccordionItemContext() {
  return useContext(AccordionItemContext);
}

// Wrapper to provide value context
interface AccordionItemWrapperProps {
  value: string;
  children: ReactNode;
  className?: string;
}

export function AccordionItemWrapper({
  value,
  children,
  className,
}: AccordionItemWrapperProps) {
  return (
    <AccordionItemContext.Provider value={value}>
      <AccordionItem value={value} className={className}>
        {children}
      </AccordionItem>
    </AccordionItemContext.Provider>
  );
}

// Compact variant for denser UIs
interface CompactAccordionProps extends AccordionProps {
  items: Array<{
    value: string;
    title: string;
    content: ReactNode;
    icon?: ReactNode;
    badge?: ReactNode;
  }>;
}

export function CompactAccordion({ items, ...accordionProps }: CompactAccordionProps) {
  return (
    <Accordion {...accordionProps}>
      {items.map((item) => (
        <AccordionItemWrapper key={item.value} value={item.value}>
          <AccordionTrigger icon={item.icon} badge={item.badge}>
            {item.title}
          </AccordionTrigger>
          <AccordionContent>{item.content}</AccordionContent>
        </AccordionItemWrapper>
      ))}
    </Accordion>
  );
}

'use client';

import { motion, AnimatePresence } from 'framer-motion';
import { ReactNode, useEffect } from 'react';
import { X } from 'lucide-react';
import { clsx } from 'clsx';
import { IconButton } from './Button';

export type DrawerSide = 'left' | 'right' | 'top' | 'bottom';
export type DrawerSize = 'sm' | 'md' | 'lg' | 'xl';

interface DrawerProps {
  isOpen: boolean;
  onClose: () => void;
  side?: DrawerSide;
  size?: DrawerSize;
  title?: string;
  subtitle?: string;
  icon?: ReactNode;
  children: ReactNode;
  footer?: ReactNode;
  closeOnBackdropClick?: boolean;
  showCloseButton?: boolean;
  className?: string;
}

const sizeClasses: Record<DrawerSide, Record<DrawerSize, string>> = {
  left: {
    sm: 'w-80',
    md: 'w-96',
    lg: 'w-[32rem]',
    xl: 'w-[42rem]',
  },
  right: {
    sm: 'w-80',
    md: 'w-96',
    lg: 'w-[32rem]',
    xl: 'w-[42rem]',
  },
  top: {
    sm: 'h-64',
    md: 'h-80',
    lg: 'h-96',
    xl: 'h-[32rem]',
  },
  bottom: {
    sm: 'h-64',
    md: 'h-80',
    lg: 'h-96',
    xl: 'h-[32rem]',
  },
};

const positionClasses: Record<DrawerSide, string> = {
  left: 'left-0 top-0 bottom-0',
  right: 'right-0 top-0 bottom-0',
  top: 'top-0 left-0 right-0',
  bottom: 'bottom-0 left-0 right-0',
};

const borderClasses: Record<DrawerSide, string> = {
  left: 'border-r-2',
  right: 'border-l-2',
  top: 'border-b-2',
  bottom: 'border-t-2',
};

const animationVariants: Record<DrawerSide, any> = {
  left: {
    initial: { x: '-100%', opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: '-100%', opacity: 0 },
  },
  right: {
    initial: { x: '100%', opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: '100%', opacity: 0 },
  },
  top: {
    initial: { y: '-100%', opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: '-100%', opacity: 0 },
  },
  bottom: {
    initial: { y: '100%', opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: '100%', opacity: 0 },
  },
};

export function Drawer({
  isOpen,
  onClose,
  side = 'right',
  size = 'md',
  title,
  subtitle,
  icon,
  children,
  footer,
  closeOnBackdropClick = true,
  showCloseButton = true,
  className,
}: DrawerProps) {
  // Lock body scroll when drawer is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen]);

  // Close on Escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };
    window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose]);

  const isVertical = side === 'left' || side === 'right';

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeOnBackdropClick ? onClose : undefined}
            className="fixed inset-0 z-40 bg-black/70 backdrop-blur-sm"
          />

          {/* Drawer Container */}
          <motion.div
            {...animationVariants[side]}
            transition={{ type: 'spring', damping: 28, stiffness: 260, mass: 0.85 }}
            className={clsx(
              'fixed z-50',
              'bg-slate-950/95',
              'border border-slate-800/80 shadow-xl shadow-black/40',
              'flex flex-col',
              positionClasses[side],
              borderClasses[side],
              sizeClasses[side][size],
              isVertical ? 'overflow-y-auto' : 'overflow-x-auto',
              className
            )}
          >
            {/* Header */}
            {(title || showCloseButton) && (
              <div className="flex items-center justify-between px-4 py-3 border-b border-slate-800/70 flex-shrink-0">
                <div className="flex items-center gap-2.5">
                  {icon && (
                    <div className="p-1.5 bg-cyan-500/10 rounded-lg border border-cyan-500/30">
                      <span className="w-4 h-4 text-cyan-400 flex items-center justify-center">
                        {icon}
                      </span>
                    </div>
                  )}
                  {title && (
                    <div>
                      <h2 className="text-sm font-semibold text-slate-50">{title}</h2>
                      {subtitle && (
                        <p className="text-xs text-slate-400 mt-0.5">{subtitle}</p>
                      )}
                    </div>
                  )}
                </div>
                {showCloseButton && (
                  <IconButton
                    icon={<X />}
                    size="sm"
                    variant="ghost"
                    onClick={onClose}
                    aria-label="Close drawer"
                  />
                )}
              </div>
            )}

            {/* Content */}
            <div className="flex-1 p-4 overflow-auto text-sm text-slate-200">
              {children}
            </div>

            {/* Footer */}
            {footer && (
              <div className="flex items-center justify-between px-4 py-3 border-t border-slate-800/70 bg-slate-900/60 flex-shrink-0">
                {footer}
              </div>
            )}
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
}
